import React from "react";
import "./App.css";
import { CATEGORIES } from "./data";
import Category from "./Category.js";
import Task from "./Task.js";

class App extends React.Component {
  state = {
    filter: "Food",
    tasks: [
      {
        text: "Buy rice",
        category: "Food"
      },
      {
        text: "Save a tenner",
        category: "Money"
      },
      {
        text: "Build a todo app",
        category: "Code"
      },
      {
        text: "Build todo API",
        category: "Code"
      },
      {
        text: "Get an ISA",
        category: "Money"
      },
      {
        text: "Cook rice",
        category: "Food"
      },
      {
        text: "Tidy house",
        category: "Misc"
      }
    ]
  };

  filteredTasks = tasks => {
    return tasks.filter(task => task.category === this.props.filter);
  };

  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <Category categories={CATEGORIES} />
        <Task tasks={this.state.tasks} filter={this.state.filter} />
      </div>
    );
  }
}

export default App;
