import React from "react";

class Task extends React.Component {
  filteredTasks = tasks => {
    return this.props.tasks.filter(task => task.category === this.props.filter);
  };
  render() {
    return (
      <div className="tasks">
        <h5>Tasks</h5>
        <div>
          {filteredTasks.map(t => {
            return (
              <div className="task">
                <div className="label">{t.category}</div>
                <div className="text">{t.text}</div>
              </div>
            );
          })}
          {console.log(this.props.tasks)}
        </div>
      </div>
    );
  }
}

export default Task;
